function angleCalc(){
  var a=$(".slide-out-nav").height(),n=$(".slide-out-nav").width(),e=Math.atan(a/n),s=(90-e*(180/Math.PI))*-1;
  $(".mobile-nav-slice").css({transform:"rotate("+s+"deg)"})
}
$(document).ready(function(){
  angleCalc()
}),$(window).scroll(function(){
  var a=$(this).scrollTop();
  a>$(this).height()/3?$(".fixed-navbar, .navbar-lockup").addClass("nav-fix"):$(".fixed-navbar, .navbar-lockup").removeClass("nav-fix")
}),$(".mobile-nav-toggle").click(function(){
  $(this).hasClass("nav-open")?($(this).removeClass("nav-open"),$(".slide-out-nav, .fixed-navbar, .mobile-shift").removeClass("nav-open")):($(this).addClass("nav-open"),$(".slide-out-nav, .fixed-navbar, .mobile-shift").addClass("nav-open"))
}),$(".anc").click(function(){($(this).removeClass("nav-open"),$(".slide-out-nav, .mobile-nav-toggle, .fixed-navbar, .mobile-shift").removeClass("nav-open"))});


document.addEventListener('DOMContentLoaded',function(event){
    var dataText = [ "Web Developer.", "Native App Developer.", "Programmer.", "Guitarist.", "Gamer.", "Blogger."];
    function typeWriter(text, i, fnCallback) {
      if (i < (text.length)) {
       document.querySelector(".type").innerHTML = text.substring(0, i+1) +'<span class="typing" aria-hidden="true"></span>';
        setTimeout(function() {
          typeWriter(text, i + 1, fnCallback)
        }, 100);
      }
      else if (typeof fnCallback == 'function') {
        setTimeout(fnCallback, 300);
      }
    }
    function StartTextAnimation(i) {
       if (typeof dataText[i] == 'undefined'){
          setTimeout(function() {
            StartTextAnimation(0);
          }, 200);
       }
       if (i < dataText[i].length) {
       typeWriter(dataText[i], 0, function(){
       StartTextAnimation(i + 1); 
       });

      }
      else{
          i=0;
          typeWriter(dataText[i], 0, function(){
           
           StartTextAnimation(i + 1);
           
          });
      }
    }
    StartTextAnimation(0);
  });

  $(document).ready(function(){
    $(window).scroll(function(){
        if(this.scrollY > 20){
            $('.navbar').addClass("sticky");
        }else{
            $('.navbar').removeClass("sticky");
        }
        if(this.scrollY > 500){
            $('.scroll-up-btn').addClass("show");
        }else{
            $('.scroll-up-btn').removeClass("show");
        }
    });
    $('.scroll-up-btn').click(function(){
        $('html').animate({scrollTop: 0});
        $('html').css("scrollBehavior", "auto");
    });

    $('.navbar .menu li a').click(function(){
        $('html').css("scrollBehavior", "smooth");
    });
    $('.menu-btn').click(function(){
        $('.navbar .menu').toggleClass("active");
        $('.menu-btn i').toggleClass("active");
    });



});

$(document).ready(function(){
  $(".menu-home").addClass("selected"),
$(window).scroll(function() {
   if ($(this).scrollTop() > 0){
    $('.menu-about').removeClass('selected');
    $('.menu-contact').removeClass('selected');
    $('.menu-skills').removeClass('selected');
    $(".menu-home").addClass("selected");
   }
   if ($(this).scrollTop() > $('.home').outerHeight()){
    $('.menu-home').removeClass('selected');
    $('.menu-contact').removeClass('selected');
    $('.menu-skills').removeClass('selected');
    $(".menu-about").addClass("selected");
   }
   if ($(this).scrollTop() > ($('.about').outerHeight() + $('.home').outerHeight())){
    $('.menu-about').removeClass('selected');
    $('.menu-contact').removeClass('selected');
    $('.menu-home').removeClass('selected');
    $(".menu-skills").addClass("selected");
   }
   if ($(this).scrollTop() > ($('.about').outerHeight() + $('.home').outerHeight() + $('.skills').outerHeight())){
    $('.menu-about').removeClass('selected');
    $('.menu-home').removeClass('selected');
    $('.menu-skills').removeClass('selected');
    $(".menu-contact").addClass("selected");
   }

})

});
